mapper.insert:

		SELECT NVL(MAX(issu_no), 0) + 1
		FROM   issue

INSERT INTO		 issue
			(   
				 issu_no
			   , prjt_no
			   , buss_no
			   , memb_id
			   , issu_ttl
			   , issu_cntn
			   , issu_knd
			   , issu_rank
			   , issu_st
		       , issu_dt																			 
			 )
	  VALUES  
	         (
				 #{issuNo}
			   , 1


			   , #{bussNo}


			   , NULL	


			   , #{membId}
			   , #{issuTtl}


			   , #{issuCntn}


		 	   ,  'No Contents'


			   ,  #{issuKnd}
			   ,  #{issuRank}
			   ,  '0E2'
			   ,  SYSDATE					
	 		   )

mapper.update:
UPDATE    issue
SET         
			    issu_ttl    = #{issuTtl}
			,   issu_cntn 	= #{issuCntn}
			,   issu_knd  	= #{issuKnd}
			,   issu_rank 	= #{issuRank}
			,   issu_st     = #{issuSt}
			,   issu_dt     = SYSDATE
WHERE    
				issu_no    	= #{issuNo}
