mapper.select:
SELECT buss_no , prjt_no , NVL(buss_upcd, 0) AS buss_upcd , buss_nm , CASE buss_imp WHEN '0J1' THEN '상' WHEN '0J2' THEN '중' WHEN '0J3' THEN '하' END AS buss_imp , buss_prgre , buss_frdt , buss_todt , buss_st FROM business WHERE (buss_st = '0K1' OR buss_st = '0K3')
AND prjt_no = #{prjtNo}
ORDER BY buss_no

SELECT buss_st, COUNT(buss_st) AS buss_st_cnt FROM business
AND prjt_no = #{prjtNo}
GROUP BY buss_st

SELECT level , buss_no , buss_nm , buss_upcd , buss_imp , buss_prgre , buss_st , TRUNC(buss_todt - sysdate,0) AS d_day , CASE WHEN buss_cmpltdt IS NOT NULL THEN TRUNC(buss_cmpltdt - buss_todt,0) ELSE -1 END AS end_delay_day FROM business
AND prjt_no = #{prjtNo}
START WITH buss_upcd IS NULL CONNECT BY PRIOR buss_no = buss_upcd

SELECT buss_nm, buss_no
FROM   BUSINESS
WHERE prjt_no = #{prjtNo}

mapper.update:
		UPDATE
		BUSINESS
		
			
				
					BUSS_UPCD = (SELECT BUSS_NO
								FROM   BUSINESS
								WHERE  BUSS_NM = #{bussUpNm}
								AND    PRJT_NO = #{prjtNo})
					WHERE BUSS_NO = #{bussNo}
				
				
					BUSS_UPCD = #{bussNo}
					WHERE BUSS_NO = (SELECT BUSS_NO
								FROM   BUSINESS
								WHERE  BUSS_NM = #{bussUpNm}
								AND    PRJT_NO = #{prjtNo})
				
				
					
					
					BUSS_UPCD = NULL
					WHERE  BUSS_NO = #{bussNo}

UPDATE business
SET buss_frdt = #{bussFrdt}, buss_todt = #{bussTodt}
 
	, buss_prgre = #{bussPrgre}

WHERE buss_no = #{bussNo}

UPDATE business
SET
buss_upcd = #{bussUpcd}
WHERE buss_no = #{bussNo}

update.foreach:
