mapper.insert:

	SELECT NVL(MAX(reply_no),0) + 1 AS replyNo, SYSDATE AS replyDt
	FROM business_reply


INSERT INTO business_reply
	(
	reply_no
	, buss_no
	, reply_cntn
	, reply_dt
	, pubcyn
	, memb_id
	, reply_upno
	)
VALUES
	(
	#{replyNo}
	, #{bussNo}
	, #{replyCntn}
	, #{reply_dt}
	, #{pubcyn}
	, memb_id
	, reply_upno
	)

mapper.update:
SELECT SYSDATE AS replyDt FROM dual
UPDATE business_reply
reply_cntn = #{replyCntn},
reply_show = #{replyShow},
reply_dt = #{replyDt}
WHERE reply_no = #{prjtFdbkNo}
