mapper.select:
SELECT f.file_no , f.prjt_no , f.buss_no , f.file_nm , f.file_path , f.file_type , f.file_desct , f.file_dt , mem.memb_nm , f.file_size FROM project_file f, project p, business b, member mem WHERE f.buss_no = b.buss_no AND b.prjt_no = p.prjt_no AND f.memb_id = mem.memb_id AND f.prjt_no = #{prjtNo}
AND f.file_type LIKE '%' || #{filter} || '%'
ORDER BY file_no DESC

mapper.insert:

SELECT NVL(MAX(FILE_NO), 0) +1
FROM   PROJECT_FILE

INSERT INTO project_file(file_no
						 , prjt_no
						 , buss_no
						 , file_nm
						 , file_path
						 , file_type
						 , file_desct
						 , memb_id
						 , file_size)
VALUES (
		#{fileNo}
		, #{prjtNo}
		, #{bussNo}
		, #{fileNm}
		, #{filePath}
		, #{fileType}
		, #{fileDesct}
		, #{membId}
		, #{fileSize}
			)

INSERT INTO project_file ( file_no , prjt_no , buss_no , file_nm , file_path , file_type , file_dt , memb_id , file_size ) SELECT file_seq.nextval, W.* FROM (
SELECT 1 as prjtNo , 1 as bussNo , #{files.fileNm} as fileNm , #{files.filePath} as filePath , #{files.fileType} as fileType , SYSDATE as fileDt , 'ojunkwon' as membId , #{files.fileSize} as fileSize FROM DUAL
) W
