mapper.insert:

SELECT NVL(MAX(FILE_NO), 0) +1
FROM   PROJECT_FILE

INSERT INTO project_file(file_no
						 , prjt_no
						 , buss_no
						 , file_nm
						 , file_path
						 , file_type
						 , file_desct
						 , memb_id
						 , file_size)
VALUES (
		#{fileNo}
		, #{prjtNo}
		, #{bussNo}
		, #{fileNm}
		, #{filePath}
		, #{fileType}
		, #{fileDesct}
		, #{membId}
		, #{fileSize}
			)

INSERT INTO project_file ( file_no , prjt_no , buss_no , file_nm , file_path , file_type , file_desct , file_dt , memb_id , file_size ) SELECT file_seq.nextval, A.* FROM (
SELECT 1 , 1 , #{file.fileNm} , #{file.filePath} , 'text' , 'dd' , SYSDATE , 'ojunkwon' , #{file.fileSize} FROM DUAL
) A
