FROM   PROJECT_FILE

INSERT INTO project_file(file_no
						 , prjt_no
						 , buss_no
						 , file_nm
						 , file_path
						 , file_type
						 , memb_id
						 , file_size)
VALUES (
		#{fileNo}
		, #{prjtNo}
		, #{bussNo}
		, #{fileNm}
		, #{filePath}
		, #{fileType}
		, #{membId}
		, #{fileSize}
			)
