mapper.insert:
INSERT INTO 	issue_hashtag
		(   	issu_ht_no
		  	  , issu_no
		  	  , issu_ht_nm																 
		 )
SELECT 			issue_hashtag_seq.nextval, A.*
FROM 	 (

	SELECT  	#{issuNo}
	      	,	#{hashtags}
	FROM        DUAL

	) A

BEGIN

DELETE 		
FROM			issue_hashtag
WHERE			issu_no = #{issuNo};

INSERT INTO 	issue_hashtag
		(   	issu_ht_no
		  	  , issu_no
		  	  , issu_ht_nm																 
		 )
SELECT 			issue_hashtag_seq.nextval, A.*
FROM 	 (

	SELECT  	#{issuNo}
	      	,	#{hashtags}
	FROM        DUAL

		 ) A;

END;
