mapper.insert:

	SELECT NVL(MAX(issu_rply_no), 0) + 1
	FROM   issue_reply

INSERT INTO 		issue_reply
			(			
				    issu_rply_no
			  ,     issu_no
			  ,     memb_id
			  ,     issu_rply_cntn
			  ,     issu_rply_dt
			  ,     issu_rply_del					
			)
			VALUES
			(
			   	    #{issuRplyNo}
			   	 ,  #{issuNo}
			   	 ,  #{membId}
			   	 ,  #{issuRplyCntn}
			   	 ,  SYSDATE
			   	 ,  0					   
			)

mapper.update:
   UPDATE 			issue_reply
   SET				issu_rply_dt    = SYSDATE
					 ,  issu_rply_cntn  = #{issuRplyCntn}
   WHERE         	issu_rply_no    = #{issuRplyNo}

mapper.delete:
        UPDATE		 issue_reply
	    SET			 issu_rply_del   = 1
	   WHERE         issu_rply_no    = #{issuRplyNo}
