mapper.update:
		UPDATE member SET memb_nm = #{membNm}
                          ,memb_dept = #{membDept}
                          ,memb_wkgd = #{membWkgd}
                          ,memb_msg = #{membMsg}
                          ,memb_birth_dt = #{membBirthDt}
                          ,memb_email = #{membEmail}
                          ,memb_telno = #{membTelno}
                          ,memb_pwd = #{membPwd}
                      WHERE memb_id = #{membId}

mapper.select:
SELECT m.memb_nm
FROM member m JOIN project_member p ON m.memb_id = p.memb_id
WHERE p.prjt_no = #{prjtNo}
