mapper.select:
SELECT pm.prjt_no , memb_id , prjt_memb_perm , prjt_nm , prjt_pubcyn , prjt_fdt , prjt_tdt , prjt_cmpltdt , prjt_st FROM project_member pm LEFT JOIN project p ON(pm.prjt_no = p.prjt_no)
AND memb_id = #{membId}
ORDER BY prjt_no

mapper.update:
UPDATE PROJECT_MEMBER
SET PRJT_FAV = #{prjtFav}
WHERE PRJT_NO = #{prjtNo}
