mapper.select:
SELECT pm.prjt_no , memb_id , prjt_memb_perm , prjt_nm , prjt_pubcyn , prjt_fdt , prjt_tdt , prjt_cmpltdt , prjt_st FROM project_member pm LEFT JOIN project p ON(pm.prjt_no = p.prjt_no)
AND memb_id = #{membId}
ORDER BY prjt_no

mapper.update:
UPDATE project_member
SET prjt_fav = #{prjtFav}
WHERE prjt_no = #{prjtNo}

UPDATE project
prjt_cmpltdt = SYSDATE,
prjt_nm = #{prjtNm}, prjt_pubcyn = #{prjtPubcyn}, prjt_fdt = #{prjtFdt}, prjt_tdt = #{prjtTdt}, PRJT_ST = #{prjtSt}
WHERE prjt_no = #{prjtNo}

mapper.select:
SELECT pm.memb_id 	
FROM   PROJECT_MEMBER pm, PROJECT p
WHERE  pm.prjt_No = p.prjt_No
AND    pm.prjt_NO  = #{prjtNo}
AND    pm.memb_id =  #{membId}
AND    p.PRJT_ST not in ('0D2')
