mapper.select:
SELECT prjt_no , buss_uncmpl_cnt , buss_cmpl_cnt , buss_exce_cnt , issu_solve_cnt , issu_unsolve_cnt , issu_unsolvable_cnt , buss_cmpl_rate , buss_uncmpl_rate , buss_exce_rate , issu_solve_rate , issu_unsolve_rate , issu_unsolvable_rate , prjt_stat_dt_cnt , prjt_memb_cnt FROM project_status
AND prjt_no = #{prjtNo}
ORDER BY prjt_no
