mapper.select:
SELECT buss_st, COUNT(buss_st) AS buss_st_cnt FROM business
AND prjt_no = #{prjtNo}
GROUP BY buss_st

SELECT level , buss_no , buss_nm , buss_upcd , buss_imp , buss_prgre , buss_st , TRUNC(buss_todt - sysdate,0) AS d_day , CASE WHEN buss_cmpltdt IS NOT NULL THEN TRUNC(buss_cmpltdt - buss_todt,0) ELSE -1 END AS end_delay_day FROM business
AND prjt_no = #{prjtNo}
START WITH buss_upcd IS NULL CONNECT BY PRIOR buss_no = buss_upcd
